import React from 'react';

import classes from './Button.module.css';

const Button = (props) => {

    let attachedClasses = [classes.Button];

    switch (props.size) {
        case "medium":
            attachedClasses.push(classes.mediumButton);
            break;
        case "large":
            attachedClasses.push(classes.largeButton);
            break;
        default:
            break;
    }

    switch (props.color) {
        case "orange":
            attachedClasses.push(classes.orangeButton);
            break;
        case "purple":
            attachedClasses.push(classes.purpleButton);
            break;
        default:
            break;
    }

    return (
        <button className={attachedClasses.join(' ')}>{props.children}</button>
    );

};

export default Button;
