import React from 'react';

import classes from './Button.module.css';

const Button = (props) => {

    let attachedClasses = [classes.Button];

    if (props.type === "highlighted") {
        attachedClasses.push(classes.highlighted);
    }

    if (props.size === "large") {
        attachedClasses.push(classes.large);
    }

    switch (props.color) {
        case "orange":
            attachedClasses.push(classes.orangeButton);
            break;
        case "purple":
            attachedClasses.push(classes.purpleButton);
            break;
        default:
            break;
    }

    return (
        <button className={attachedClasses.join(' ')}>{props.children}</button>
    );

};

export default Button;
