import React, { useState } from 'react';

import Button from '../UI/Button/Button';
import Links from './Links/Links';

import './Header.css';

import logo from '../../assets/Logo/logo.svg';
import userIcon from '../../assets/Header/user.svg';
import burgerMenuIcon from '../../assets/Header/burger-menu.svg';

const Header = () => {

    let [burgerMenuDisplay, setBurgerMenuDisplay] = useState(false);
    let displayStatus;

    if (burgerMenuDisplay) {
        displayStatus = "shownMenu";
    } else {
        displayStatus = "hiddenMenu";
    }

    return (
        <>
        <header className="Header">
            <div className="burgerMenu">
                <img src={burgerMenuIcon} onClick={() => setBurgerMenuDisplay(!burgerMenuDisplay)} alt="Burger menu"/>
            </div>
            <div className="logoAndLinksPart">
                <img src={logo} alt="Logo"/>
                <Links/>
            </div>
            <div className="options">
                <Button>Sign in</Button>
                <Button size="medium" color="orange">Sign up</Button>
            </div>
            <div className="user">
                <img src={userIcon} alt="User"/>
            </div>
        </header>

        </>
    );
};

export default Header;
